{
	/**
	 * Implementation
	 */
	class StateMockProvider {
		constructor() {
			// Mock $state object
			this.$state = {
				currentState(state) {
					this.includes.withArgs(state).returns(true);
				},
				reload: sinon.stub(),
				go: sinon.stub(),
				includes: sinon.stub().returns(false),
				params: {}
			};
		}

		$get() {
			return this.$state;
		}
	}

	/**
	 * Registration
	 */
	angular
		.module("ogAngularMocks")
		.provider("$stateMock", StateMockProvider);

	/**
	 * Dependencies
	 */
	StateMockProvider.$inject = [];
}
